import axios from 'axios';

const instance = axios.create({
    baseURL:'http://localhost:3001',
    header:{
        'Content-Type': 'application/json;charset=UTF-8',
        "Access-Control-Allow-Origin": "*",
    }
});

instance.defaults.headers.common['Authorization'] = `AUTH TOKEN`;

instance.interceptors.request.use(
    config => {
        const token = (localStorage.getItem('user'));
        if(!config.headers.Authorization) {
            // config.headers['Authorization'] = token;
            const newToken = JSON.parse(token);
            console.log('Here ' +  JSON.stringify(token))
            console.log(typeof newToken);
            console.log(newToken.accessToken);
            const newAccessToken = newToken.accessToken;
            console.log ('!!!!!!' + newAccessToken);
            console.log(typeof newToken.accessToken);
            config.headers['Authorization'] = newAccessToken;
            return config;
        }
        config.headers['Content-Type'] = 'application/json';


        return config;
    },
    error => {
        Promise.reject(error);
    }
)

//instance.defaults.headers.common['Authorization'] = `${newToken}`;

export default instance;
