import React, {Component, Fragment, useContext} from 'react';
import './UserProfileStyles.css'
import { Input } from '@material-ui/core';
import LoginForm from '../Forms/LoginForm';
import {UserFromContext} from '../Forms/LoginForm'

//const value = useContext(UserFromContext);

export class UserProfile extends Component{
    //value = useContext(UserFromContext)
   render(){
       return (

            <Fragment>
                <div className = 'container'>
                    <div className= 'row justify-content-md-center' id = 'mainInfo'>
                       <div className = 'col-xs-12 col-lg-3 ' id = 'profilePicture'>
                           <div id = 'avatar'>
                               {/* <input type ="file" /> */}
                           </div>
                       </div>
                       <UserFromContext.Consumer>
                           {(context) => (
                               <Fragment>
                                   <p>
                                       {context.state.nick}
                                       <span className = 'userNick'>@</span><span className = 'userNick'> {context.state.nick} </span>
                                   </p>
                               </Fragment>
                           )}
                       </UserFromContext.Consumer>
                        <div className = 'col-xs-12 col-lg-3 ' id = 'profileDescription' >
                            <p id = 'userName'> user name</p>

                            <span className = 'userNick'>@</span><span className = 'userNick'> {this.state} </span>
                        </div>

                    </div>
                </div>
            </Fragment>

            )

    }
}

export default UserProfile;
