import axios from 'axios';

const instance = axios.create({
    baseURL:'http://localhost:3001',
    header:{
        'Content-Type': 'application/json;charset=UTF-8',
        "Access-Control-Allow-Origin": "*",
    }
});

instance.defaults.headers.common['Authorization'] = `${localStorage.getItem('user')}`;

instance.interceptors.request.use(
    response => {
        const token = (localStorage.getItem('user'));
        if(!response.headers.Authorization) {
            response.headers['Authorization'] = token;
            const newToken = JSON.parse(token);
            console.log('Here ' +  JSON.stringify(token))
            console.log(typeof newToken);
            console.log(newToken.accessToken);
            console.log(typeof newToken.accessToken);
        }
        response.headers['Content-Type'] = 'application/json';
        return response;
    },
    error => {
        Promise.reject(error);
    }
)

export default instance;
