import React, { Component, Fragment } from 'react'
import {Link, Redirect} from 'react-router-dom';
import './FormsStyles.css'
import BackDrop from 'particles-bg';
import axios from 'axios';
import UserPage from '../User/UserProfile';

import Form from "react-validation/build/form";
import Input from "react-validation/build/input";
import CheckButton from "react-validation/build/button";
// import { isEmail } from "validator";

import AuthService from "../";

const required = value => {
    if (!value){
        return (
        <div className="alert alert-danger" role="alert">
            This field is required!
        </div>
        )
    }
}

// const email = value => {
//     if (!isEmail(value)) {
//       return (
//         <div className="alert alert-danger" role="alert">
//           This is not a valid email.
//         </div>
//       );
//     }
//   };


export class LoginForm extends Component {
    constructor (props) {
        super (props);
        this.state = {
            nick:'',
            password:'',
            loading:false,
            message:''
        }
    }

    changeHandler = event => {
        this.setState({[event.target.name]: event.target.value})
    }

    headers = {
        'Content-Type': 'application/json;charset=UTF-8',
        "Access-Control-Allow-Origin": "*",
    }

    handleLogin = event => {
    event.preventDefault();
    this.setState({
        message:'',
        loading:true,
    });

    this.form.validateAll();

    if (this.checkBtn.context._errors.length === 0){
        AuthService.login(this.state.nick, this.state.password).then(
            () => {
                this.props.history.push('/userprofile');
                window.location.reload();
            },
            error => {
                const resMessage = (
                    error.responce &&
                    error.response.data &&
                    error.response.data.message) ||
                    error.message ||
                    error.toString();

                    this.setState({
                        loading: false,
                        message: resMessage
                      });
            }
        );
    } else {
        this.setState ({
            loading:false
        })
    }
    // console.log(this.state);
    // axios.post('http://localhost:3001/login', this.state, {'headers':this.headers})
    // .then (response => {
    //     console.log (response)
    // })
    // .catch (error => {
    // console.log('!Error ' + error);
    // })
}

    render() {
    const {nick, password, loading, message,} = this.state;
    return (
        <div>
        <div className = 'container'>
            <div className = 'row'>
                <div className = 'col-xs-12 offset-lg-2 col-lg-8'>
                    <div className ="form">
                        <form onSubmit = {this.handleLogin}  className="login-form">
                            <p id='logo2'> fruitful </p>

                            <input  type="text" 
                                    placeholder="nick" 
                                    name = 'nick' 
                                    value = {nick} 
                                    onChange = {this.changeHandler}
                                    validations={[required]} />

                            <input  type="password" 
                                    placeholder="password" 
                                    name = 'password' 
                                    value = {password} 
                                    onChange = {this.changeHandler}
                                    validations={[required]} />

                            <button type = 'submit'
                                    disabled={loading} > 
                                    {loading && (
                                    <span className="spinner-border spinner-border-sm"></span>
                                    )}
                                    <span>Login</span>
                                    </button>

                                    {message && (
                                    <div className="form-group">
                                        <div className="alert alert-danger" role="alert">
                                            {message}
                                        </div>
                                     </div>
                                    )}

                            <p id="message"> don't have account ? <Link to = '/signup' >  sign up  </Link> </p>

                            <CheckButton style={{ display: "none" }} ref={c => {this.checkBtn = c;}}/>
                        </form>
                    </div>
                </div>
            </div>
        </div>
        <BackDrop style={{height: "100%" , color:'#CD5C5C'}} num={7} type="square" bg={true} />
        </div>
    )
    }
}

export default LoginForm
