import React, { Component, Fragment } from 'react'
import {Link, Redirect} from 'react-router-dom';
import './FormsStyles.css'
import axios from 'axios';
import UserPage from '../User/UserProfile';

import Form from "react-validation/build/form";
import Input from "react-validation/build/input";
import CheckButton from "react-validation/build/button";
// import { isEmail } from "validator";

import AuthService from "../../services/authservice";

const required = value => {
    if (!value){
        return (
        <div className="alert alert-danger" role="alert">
            This field is required!
        </div>
        )
    }
}

class LoginForm extends Component {
    constructor (props) {
        super (props);
        this.state = {
            nick:'',
            password:'',
            loading:false,
            message:''
        }
    }

    changeHandler = event => {
        this.setState({[event.target.name]: event.target.value})
    }

    handlerLogin = event => {
        event.preventDefault();

        this.setState({
            loading:true,
            message:''
        });

        this.form.validateAll();

        if (this.checkBtn.context._errors.length === 0) {
            AuthService.login(this.state.username, this.state.password).then(
              () => {
                this.props.history.push("/userprofile");
                window.location.reload();
              },
              error => {
                const resMessage =
                  (error.response &&
                    error.response.data &&
                    error.response.data.message) ||
                  error.message ||
                  error.toString();

                this.setState({
                  loading: false,
                  message: resMessage
                });
              }
            );
          } else {
            this.setState({
              loading: false
            });
          }
    }

    render() { 
        const {nick, password, loading, message} = this.state;
        return (
        <div>
            <div className = 'container'>
            <div className = 'row'>
                <div className = 'col-xs-12 offset-lg-2 col-lg-8'>
                    <div className ="form">
                        <form onSubmit={this.handleLogin} className="login-form">
                            <p id='logo2'> fruitful </p>
                            <input  type="text" 
                                    placeholder="nick" 
                                    name = 'nick' 
                                    value = {nick}
                                    onChange = {this.changeHandler}
                                    validations={[required]} />
                            <input  type="password" 
                                    placeholder="password" 
                                    name = 'password'
                                    value= {password}
                                    onChange = {this.changeHandler}
                                    validations />
                            <p id="message"> don't have account ? <Link to = '/signup' >  sign up  </Link> </p> 
                        </form>
                    </div>
                </div>
            </div>
        </div>
    </div>
    )}
}

export default LoginForm
