import React, { Component} from 'react'
import {Link} from 'react-router-dom';
import './FormsStyles.css';
import axios from 'axios';
//import UserPage from '../User/UserProfile';

//import Form from "react-validation/build/form";
//import Input from "react-validation/build/input";
//import CheckButton from "react-validation/build/button";
// import { isEmail } from "validator";

import AuthService from "../../services/authservice";

const required = value => {
    if (!value){
        return (
        <div className="alert alert-danger" role="alert">
            This field is required!
        </div>
        )
    }
}

class LoginForm extends Component {
    constructor (props) {
        super (props);
        this.state = {
            nick:'',
            password:'',
            loading:false,
            message:''
        }
    }

    changeHandler = event => {
        this.setState({[event.target.name]: event.target.value})
    }

    handlerLogin = event => {
      console.log(this.state)
        event.preventDefault();

        this.setState({
            loading:true,
            message:''
        });

      axios.post('http://localhost:3001/login', this.state)
      .then (response => {
        console.log ('Login axios')
        if (response.data.accessToken){
            localStorage.setItem('user', JSON.stringify(response.data));
        }
        return response.data;
    });

            // AuthService.login(this.state.username, this.state.password).then(
            //   () => {
            //     this.props.history.push("/userprofile");
            //     window.location.reload();
            //   },
            //   error => {
            //     const resMessage =
            //       (error.response &&
            //         error.response.data &&
            //         error.response.data.message) ||
            //       error.message ||
            //       error.toString();

            //     this.setState({
            //       loading: false,
            //       message: resMessage
            //     });
            //   }
            // );
    }

    render() { 
        const {nick, password, loading, message} = this.state;
        return (
        <div>
            <div className = 'container'>
            <div className = 'row'>
                <div className = 'col-xs-12 offset-lg-2 col-lg-8'>
                    <div className ="form">
                        <form onSubmit={this.handlerLogin} className="login-form">
                            <p id='logo2'> fruitful </p>

                            <input  type="text" 
                                    placeholder="nick" 
                                    name = 'nick' 
                                    value = {nick}
                                    onChange = {this.changeHandler}
                                    validations={[required]} />
                            <input  type="password" 
                                    placeholder="password" 
                                    name = 'password'
                                    value= {password}
                                    onChange = {this.changeHandler}
                                    validations = {[required]} />

                            <div className="form-group">
              <button
                className="btn btn-primary btn-block"
                disabled={loading}
              >
                {loading && (
                  <span className="spinner-border spinner-border-sm"></span>
                )}
                <span>Login</span>
              </button>
            </div>

            {message && (
              <div className="form-group">
                <div className="alert alert-danger" role="alert">
                  {message}
                </div>
              </div>
            )}


                            <p id="message"> don't have account ? <Link to = '/signup' >  sign up  </Link> </p> 
                        </form>
                    </div>
                </div>
            </div>
        </div>
    </div>
    )}
}

export default LoginForm
