import React, {Component, Fragment} from 'react';
import instance from '../../services/tokenprovider';
import './ToDoStyles.css'

export class AllToDo extends Component{
    constructor(props){
        super(props);
        this.state = {
            todos:[]
        }
    }

    componentDidMount(){
        instance.get('showalltodos')
        .then(responce => {
            const todos = responce.data;
            this.setState({
                todos
            })
        })
    }

    delete(key){
        this.setState(prevSave => ({
            data:prevSave.data.filter(el => el !=key)
        }))
    }

    handleDelete(key){
        this.props.handleDelete(key);
    }
    render(){
        return(
            <Fragment>
                <div className = 'container'>
                    <div className = 'row'>
                        <form >
                        <div className = 'offset-lg-2 col-lg-8'>
                            {this.state.todos.map(todos => 
                            <div key = {todos.id} className = 'todosItem'> {todos.text} <hr></hr>
                            <p>{todos.id}</p>
                            <div>
                            <button type ="submit" id= 'deleteBtn' onClick = {this.handleDelete}> Delete</button> 
                            <button id= 'completeBtn'> Complete </button> </div> </div>)}
                        </div>
                        </form>
                    </div>
                </div>
            </Fragment>
        )
    }
}

export default AllToDo;
