import React, {Component, Fragment} from 'react';
import instance from '../../services/tokenprovider';
import './ToDoStyles.css'

export class AllToDo extends Component{
    constructor(props){
        super(props);
        this.state = {
            todos:[]
        }
    }

    componentDidMount(){
        instance.get('showalltodos')
        .then(responce => {
            const todos = responce.data;
            this.setState({
                todos
            })
        })
    }

    upComplete(id){
        instance.get('delete/:id', {
            id:id
        })
        .then(responce => {
            const todos = responce.data;
            this.setState({
                todos
            })
        })
        .catch(error => {
            console.log(error);
        })
    }

    render(){
        return(
            <Fragment>
                <div className = 'container'>
                    <div className = 'row'>

                        <div className = 'offset-lg-2 col-lg-8'>
                            {this.state.todos.map(todos => 
                            <div key = {todos.id} className = 'todosItem'> {todos.text} <hr></hr>
                            <p>{todos.id}</p>
                            <div>

                            <button  id= 'deleteBtn'> Delete</button> 
                            <button id= 'completeBtn' onClick = {() => this.deleteI}> Complete </button> </div>

                             </div>)}
                        </div>

                    </div>
                </div>
            </Fragment>
        )
    }
}

export default AllToDo;
