import React, {Component, Fragment} from 'react';
import ToDoList from './ToDoList';
import shortid from 'shortid';
import './ToDoStyles.css';

export default class ToDoForm extends Component {
    constructor(props){
        super(props);

        this.state = {
            text: '',
        }
    }

    enterToDo = event => {
        this.setState({ [event.target.name]: event.target.value })
    }

    sumbitToDo = event => {
        event.preventDefault();
        this.props.onSubmit({
            id:shortid.generate(),
            text:this.state.text,
            complete:false
        });

        this.setState({
            text:'',
        })

    }

    render(){
        const {text} = this.state;
        return (
            <Fragment>
                <div className = 'container'>
                    <div className = 'row'>
                        <div className = 'col-lg-6'>
                            <form onSubmit = {this.sumbitToDo} className = 'addToDoForm'>
                                <input name = 'text' value = {text} onChange = {this.enterToDo} placeholder = 'to do...'/>
                                <button onChange = {this.sumbitToDo}> add </button>
                            </form>
                        </div>
                        <div className = 'col-lg-6'>
                            <div className = 'addToDoPicture'>
                            <img id = 'pictureMainOnPage' 
                            src={`${process.env.PUBLIC_URL}/img/mainPicture.jpeg`} 
                            alt="logo"/>
                            </div>
                        </div>
                    </div>
                </div>
            </Fragment>
        )
    }
}
